clear all; clc; close all;load('ex3data1.mat');X = X.';npixelsrow = 20; % From the exercise pdfnpixelscol = 20; % From the exercise pdfXtemp = reshape(X,npixelsrow,round(numel(X)/npixelsrow));ImgX = zeros(npixelsrow*100,npixelscol*50);for Rownumb = 1:100  for Colnumb = 1:50    imgnumb = Rownumb*Colnumb;    imgmat = Xtemp(:,(imgnumb-1)*npixelscol+1:imgnumb*npixelscol);        rowstart = (Rownumb-1)*npixelsrow+1;    rowend = Rownumb*npixelsrow;    colstart = (Colnumb-1)*npixelscol+1;    colend = Colnumb*npixelscol;    ImgX(rowstart:rowend,colstart:colend) = imgmat;          endenddisplay('Rearrangement Done');imshow(ImgX)display('Image Display Done');